    int longestSubstring(string s, int k) {
        int n = (int)s.size();
        if (n < k) return 0;

        vector<int> vec(26, 0);
        for(char c : s) {
            vec[c - 'a']++;
        }

        int pos = 0;
        for (int i = 0; i < n; i++) {
            if (vec[s[i]-'a'] < k) {
                break;
            }
            pos++;
        }
        if (pos == n) {
            return n;
        }
        int left = longestSubstring(s.substr(0,pos), k);
        while (pos < n) {
            if (vec[s[pos]-'a'] < k) {
                pos++;
            } else {
                break;
            }

        }
        int right = longestSubstring(s.substr(pos), k);
        return max(left, right);
    }

    //
    // 给定一个字符串s,字符串中每个字符至少出现k次
    // 求最长子字符串的长度
    void Leetcode::main_395() {
        cout << "3 : " << longestSubstring("aaabb", 3) << endl;
        cout << "5 : " << longestSubstring("ababbc", 2) << endl;
        cout << "0 : " << longestSubstring("ababacb", 3) << endl;
        string ss = "";
        cout << "0 : " << longestSubstring(ss, 301) << endl;
    }
